import React from "react"
import PropTypes from "prop-types"

class Apartments extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      apartments: []
    }
  }

  componentDidMount(){
    fetch('/apartments')
    .then(resp => {
      return resp.json()
    })
    .then(apartmentsJson => {
      this.setState({apartments: apartmentsJson})
    })
  }

  render () {
    const { apartments } = this.state
    return (
      <React.Fragment>
      <div id="list">
        <h1>Apartments</h1>

        { 
            apartments.map((apartment, i) => {
              return <div class="card card2 text-center" key={i}>
                      <div class="card-header">
                        New Listing
                      </div>
                      <div class="card-body">
                        <h5 class="card-title">Street Address: {apartment.address1}</h5>
                        <h5 class="card-title">Unit/Apartment Number: {apartment.address2}</h5>
                        <h5 class="card-title">City: {apartment.city}</h5>
                        <h5 class="card-title">State: {apartment.state}</h5>
                        <h5 class="card-title">Zip Code: {apartment.zipcode}</h5>
                        <p class="card-text">Manager: {apartment.manname} | Phone number: {apartment.phone} | Contact {apartment.hours}</p>
                        <a href="#" class="btn btn-primary">More info</a>
                      </div>
                    </div>
            })
          }
      </div>
      </React.Fragment>
    );
  }
}

export default Apartments
